INSERT INTO `μέλος` VALUES ('Ηρώ','kelesidou@inzone.gr','Κελεσίδου','Συντάκτης'),('Δημήτρης','koufogiannidis@inzone.gr','Κουφογιαννίδης','Proofreader'),('Χρήστος','ksilakis@inzone.gr','Ξυλάκης','Εξωτερικός συνεργάτης'),('Βασίλης','lykoudis@inzone.gr','Λυκούδης','Συντάκτης'),('Δημήτρης','natsiopoulos@inzone.gr','Νατσιόπουλος','Συντάκτης'),('Χρήστος','ntaoulas@inzone.gr','Νταούλας','General Manager'),('Μαρία','roumeliwtou@inzone.gr','Ρουμελιώτου','Αρχισυντάκτης'),('Μαριάννα','sifaki@inzone.gr','Σηφάκη','Εξωτερικός συνεργάτης'),('Μαριάνθη','tentzeraki@inzone.gr','Τεντζεράκη','Συντάκτης'),('Ηλέκτρα','xatzidimitriou@inzone.gr','Χατζηδημητρίου','Συντάκτης');
